CREATE TABLE mytable(
   id               INTEGER  NOT NULL PRIMARY KEY 
  ,name             VARCHAR(16) NOT NULL
  ,cardType         VARCHAR(7) NOT NULL
  ,state            VARCHAR(6)
  ,evolvesFrom      VARCHAR(10)
  ,cardFunction     VARCHAR(51)
  ,monsterHP        INTEGER 
  ,Skill1DMG        INTEGER 
  ,Skill1EnergyQty  INTEGER 
  ,Skill1EnergyType VARCHAR(33)
  ,Skill1Effect     VARCHAR(4)
  ,Skill2DMG        INTEGER 
  ,Skill2EnergyQty  INTEGER 
  ,Skill2EnergyType VARCHAR(33)
  ,Skill2Effect     VARCHAR(4)
);
